INSERT INTO `books` (`id`, `category`, `subcategory`, `BookName`, `AuthorName`, `ReleaseDate`, `AboutTheBook`, `AboutTheAuthor`, `price`, `quantity`, `classification`, `store`, `picture`, `mostpopular`, `posting_date`) VALUES
(3, 2, 1, 'رواية في بيتنا رجل', 'احسان عبدالقدوس', 1961, 'تتحدث الرواية عن الفترة ما قبل ثورة 23 يوليو، ولقيت صدى كبيراً آن صدورها في الوسط الثقافي والشعبي المصري، وقد ساعدت في جعل إحسان عبد القدوس في صفوف الكتّاب المتميزين، لأطروحته الفكرية الملتزمة من جانب ولبراعة أسلوبه الشائق في تناول مرحلة شديدة التعقيد سياسياً.', 'إحسان عبد القدوس (1 يناير 1919 - 12 يناير 1990) كاتب وروائي مصري. أحد أوائل الروائيين العرب الذين تناولوا في قصصهم الحب البعيد عن العذرية، وتحولت أغلب قصصه إلى أفلام سينمائية. يمثل أدب إحسان عبد القدوس نقلة نوعية متميزة في الرواية العربية، إذ نجح في الخروج من المحلية إلى حيز العالمية، وترجمت معظم رواياته إلى لغات أجنبية متعددة. وهو ابن السيدة روز اليوسف مؤَسِسَة مجلة روز اليوسف ومجلة صباح الخير. أما والده فهو محمد عبد القدوس كان ممثلاً ومؤلفاً مصريا.', 5, 'in stock', 'novels', 'جملون', 'hhh.jfif', '', '2022-04-16 14:26:10'),
(4, 2, 1, 'رجال من الشمس', 'غسان كنفاني', 1963, 'يمثل غسان كنفاني مؤلف هذه الرواية نموذجاً خاصاً للكاتب السياسي والروائي والقاص والناقد، فكان مبدعاً في كتاباته كما كان مبدعاً في حياته ونضاله واستشهاده. وقد نال عام 1966 جائزة أصدقاء الكتاب في لبنان \"لأفضل رواية عن روايته\" ما تبقى لكم كما نال جائزة منظمة الصحافيين العالمية (I.O.J) عام 1974، ونال جائزة \"اللوتس\" التي يمنحها اتحاد كتاب آسيا وأفريقيا عام 1975. وتعد روايته رجال في الشمس التي صدرت في بيروت عام 1963 من أوائل الأعمال الروائية الفلسطينية التي تكتب عن التشرد والموت والحيرة. وفيها يروي حكاية ثلاثة فلسطينيين من أجيال مختلفة، يلتقون حول ضرورة إيجاد حل فردي لمشكلة الإنسان الفلسطيني المعيشية عبر الهرب إلى الكويت، حيث النفط والثروة. أبو قيس. الرجل العجوز الذي يحلم ببناء غرفة في مكان ما خارج المخيم، أسعد: الشاب الذي يحلم بدنانير الكويت وبحياة جديدة، ومروان: الصغير الذي يحاول أن يتغلب على مأساته المعيشية، فشقيقه في الكويت تركهم دون معيل لأنه تزوج، والده ترك أمه ليتزوج بامرأة تملك بيتاً عليه إذن أن يعيل العائلة فيقرر الوصول إلى الكويت. يدين غسان كنفاني في روايته كل الأطراف التي تسببت في نكبة فلسطين، القيادات العاجزة،والقيادات الخائنة،والشعب المستسلم،والذين تخلوا عن الأرض ليبحثوا عن خلاصهم الخاص', 'غسان كنفاني (عكا 9 أبريل 1936 - بيروت 8 يوليو 1972) هو روائي وقاص وصحفي فلسطيني، ويعتبر غسان كنفاني أحد أشهر الكتاب والصحافيين العرب في القرن العشرين. فقد كانت أعماله الأدبية من روايات وقصص قصيرة متجذرة في عمق الثقافة العربية والفلسطينية. \r\n\r\nولد في عكا، شمال فلسطين، في التاسع من نيسان عام 1936م، وعاش في يافا حتى أيار 1948 حين أجبر على اللجوء مع عائلته في بادئ الأمر إلى لبنان ثم إلى سوريا. عاش وعمل في دمشق ثم في الكويت وبعد ذلك في بيروت منذ 1960 وفي تموز 1972، استشهد في بيروت مع ابنة أخته لميس في انفجار سيارة مفخخة على أيدي عملاء إسرائيليين.\r\n\r\n', 6, 'in stock', 'novels', 'sanad books', 'OIP11.jfif', '', '2022-04-16 14:26:10'),
(5, 2, 1, 'رؤوس الشياطين', 'أيمن العتوم', 2020, '                                                                                                        رواية غير اعتيادية على الإطلاق.  لأول مرة أيمن العتوم يضع قلمه على طاولة الطبيب النفسي ليكشف عن صراع الشخصيات بداخله.  فهل يكشف الطبيب عن كاتب مجنون ، أم أن الكاتب سيفلح في الولوج إلى عقل الطبيب وما يدور في رؤوس الشياطين.                                                                                                ', '                                                                                                        أيمن العتوم شاعر وروائي أردنيّ (ولد في الأردن - جرش سوف 2 آذار 1972)، تلقّى تعليمه الثانوي في دولة الإمارات العربية المتحدة - إمارة عجمان والتحق بـجامعة العلوم والتكنولوجيا الأردنية ليتحصل على بكالوريوس الهندسة المدنية فيها عام 1997 وفي عام 1999 تخرّج في جامعة اليرموك بشهادة بكالوريوس لغة عربية، ثمّ التحق بالجامعة الأردنية ليُكمل مرحلة الدراسات العليا في اللغة العربية وحصل على شهادتي الماجستير والدكتوراة في اللغة العربية تخصص نَحو ولغة عامي 2004 و2007، اشتُهر بروايته يا صاحبي السجن التي صدرت عام 2012 وتعبّر عن تجربة شخصيّة للكاتب في السجون الأردنية خلال عامَي 1996 و 1997 بكونه معتقلاً سياسياً. كما له دواوين شعريّة عديدة أحدثها ديوان \"طيور القدس عام 2016\".                                                                                                ', 9, 'in stock', 'novels', 'Ayman Otoom', 'OIP12.jfif', 'y', '2022-04-16 14:26:10'),
(6, 2, 1, 'ذكريات شتاء عن مشاعر صيف و العذبة', 'دوستويفسكي', 1876, '\r\n\" ذكريات شتاء عن مشاعر صيف \" و \" العذبة \" ترجمة : د. سامي الدروبي. في العام ١٨٦٢، قام دوستويفسكي بأول رحلة له إلى الخارج. فمر بألمانيا ووصل إلى باريس، حيث لم يمكث فيها إلا عشرة أيام، ثم سافر إلى لندن لمدة أسبوعين، عاد بعدها إلى باريس ثانية، فقضى فيها أسبوعين آخرين، ثم تركها إلى جنيف مارًا بمدينة بال. وفي جنيف التقى بصديقه نيقولا ستراخوف، فزار الصديقان إيطاليا معًا. وقد كتب ستراخوف بعد ذلك يقول: \"لا الطبيعة ولا المباني ولا آثار الفن كانت تعنيه، فإنما كان ينصرف انتباهه كله إلى الناس.\" إن هذا الغائص العظيم إلى أعماق النفوس يوجه انتباهه كله إلى البشر في الشوارع وفي المسارح وفي المقاهي. إنه يحاول أن يفهم سيكولوجية كل شعب أثناء هذه الرحلة الخاطفة التي استغرقت نحو شهرين. وعندما نشر دوستويفسكي هذه \"الذكريات\" لم يتحدث فيها عن رحلته إلا قليلًا، وإنما هو يستخدم هذه الرحلة ليعرض آراءه في تاريخ روسيا وفي وضعها، وليتهكم على البلاد التي مر بها، ليتهكم على ألمانيا وإنجلترا، وعلى فرنسا خاصةً، حيث يستهل الكلام عنها بجملة قالها فونفيرين، وهي أن \"الفرنسي محروم من العقل، ولو أوتي عقلًا لعدّ ذلك أكبر شقاء يصيبه\". ثم لا يذكر إيطاليا أو سويسرا بخير أو شر. أمّا رواية \"العذبة\" فهي واحدة من أشهر أعمال دوستويفسكي، حيث نجده يغوص في النفس البشرية لرجل يقف أمام جثة زوجته. ويأخذنا إلى ماضي العلاقة بين رجل تصرّف بقساوة مع امرأته، حتى لم يعد ينفع تراجعه الذي لم يعلنه لها، فانتحرت.', 'فيودور ميخايلوفيتش دوستويفسكي (بالروسية: Фёдор Миха́йлович Достое́вский)‏; أص‌د: [ˈfʲɵdər mʲɪˈxajləvʲɪtɕ dəstɐˈjɛfskʲɪj]  ( سماع)؛ (11 نوفمبر 1821 - 9 فبراير 1881) هو روائي وكاتب قصص قصيرة وصحفي وفيلسوف روسي. وهو واحدٌ من أشهر الكُتاب والمؤلفين حول العالم. رواياته تحوي فهماً عميقاً للنفس البشرية كما تقدم تحليلاً ثاقباً للحالة السياسية والاجتماعية والروحية لروسيا في القرن التاسع عشر، وتتعامل مع مجموعة متنوعة من المواضيع الفلسفية والدينية.\r\n\r\n', 7, 'in stock', 'novels', 'unibookjo', 'OIP13.jfif', '', '2022-04-16 14:26:10'),
(7, 2, 1, 'ذائقة الموت', 'أيمن العتوم', 2013, 'سيقولون أحبّ فتاةً أكبر منه؟! كان مُحتاجًا إلى حنانها وعطفها لا إلى حُبّها وقلبها، وليكن؛ أنا نُثارةٌ في مهبّ الرّيح أحتاج مَنْ تضمّني إلى صدرها. سيقولون: مجنون يكاد ينتهي به المطاف في الشّارع بلا وجه، وليكنْ، أفَكانَ لي هذا الوجه وأنا أتبع أبي في الهضبات الصّاعدات إلى قمّة ابن جُبير. سيقولون: أفقدتْهُ الكتب عقلَه، كان قبلها بلا قلب، وصار بعدها بلا عقل. الكتب الّتي قرأها أعاشتْه فيها، وفصَلَتْه عن الواقع؛ فلم يَعُدْ هو هو، وليكنْ؛ دلّوني على أحدٍ يستطيع أن يقول إنّه هو هو!! سيقولون: دمّرتْه عيناها، وهو يغوص فيهما ريشةً من جناح نورسٍ تتأرجح على رَهْو البحر، وليكنْ، أفكان لي قَدَرٌ أجمل من أن أغرق في بحرهما؟!! سيقولون: نضج قبل أوانه، واحترق قبل نُضجه! وليكنْ، أنا في الحبّ أعيش في غابات استوائيّة لا تعترف بالفصول ميزانًا للنُّضج، ولا تعترف بالحرارة وسيلة للاحتراق. أنا أحترق في ذاتي من أجل ذاتي، أنا أموتُ في سبيل ألاّ أفقدَني...!!', 'أيمن العتوم شاعر وروائي أردنيّ (ولد في الأردن - جرش سوف 2 آذار 1972)، تلقّى تعليمه الثانوي في دولة الإمارات العربية المتحدة - إمارة عجمان والتحق بـجامعة العلوم والتكنولوجيا الأردنية ليتحصل على بكالوريوس الهندسة المدنية فيها عام 1997 وفي عام 1999 تخرّج في جامعة اليرموك بشهادة بكالوريوس لغة عربية، ثمّ التحق بالجامعة الأردنية ليُكمل مرحلة الدراسات العليا في اللغة العربية وحصل على شهادتي الماجستير والدكتوراة في اللغة العربية تخصص نَحو ولغة عامي 2004 و2007، اشتُهر بروايته يا صاحبي السجن التي صدرت عام 2012 وتعبّر عن تجربة شخصيّة للكاتب في السجون الأردنية خلال عامَي 1996 و 1997 بكونه معتقلاً سياسياً. كما له دواوين شعريّة عديدة أحدثها ديوان \"طيور القدس عام 2016\".', 11, 'in stock', 'novels', 'Ayman Otoom', 'OIP15.jfif', '', '2022-04-16 14:26:10'),
(8, 2, 1, 'ديوان نيتشه', 'فريدريك نيتشه', 1908, '\r\nندر أن ترك مفكر وفيلسوف في الغرب أو الشرق الأثر الذي تركه المفكر والشاعر الالماني فريدريك نيتشه، سواء في عصره أو في العصور اللاحقة. فقد استطاع نيتشه وبخاصة في كتابه الشهير \"هكذا تكلم زرادشت\" أن يحدث زلزالاً حقيقياً في المفاهيم والقيم السائدة ما تزال تردداته المتواصلة تترك أثرها الكبير في نفوس الأجيال المتعاقبة، ليس في ألمانيا وحدها فحسب بل في كل أرجاء الأرض. على أن معرفة القراء العرب بنيتشه تقتصر في الأعم الغالب على الجانب الفلسفي من شخصيته، في حين أن الكثيرين لا يعرفون شيئاً عن وجوه إبداعه المتصلة بالموسيقى والمسرح، وبالشعر على وجه الخصوص. لذلك فإن الكتاب الشعري الصادر عن دار الجمل في ألمانيا والذي نقله الى العربية الشاعر والكاتب التونسي محمد بن صالح بعنوان \"ديوان نيتشه\" يسد ثغرة واسعة في هذا المجال ويعرّفنا أكثر فأكثر الى وجوه هذه الشخصية الفريدة في إبداعها وتجربتها وتكوينها المعقد. وليس من قبيل الغرابة بالطبع أن يخوض نيتشه غمار الشعر بدءاً من مراهقته الأولى في منتصف القرن التاسع عشر وحتى الفترة التي أفضت به الى الجنون قبيل موته بسنوات عدة. ذلك ان في فلسفته القائمة على القوة والتفرد وإعلاء الذات ما يتلاءم مع الشعر الى حد بعيد وما يجعل من هذا الفن الشديد الخصوصية وسيلة إضافية من وسائل تعبيره عن آرائه الجريئة وأفكاره الجامحة، خاصة أن نيتشه قد اعتبر في إحدى المراحل بأن الوجود نفسه هو ظاهرة جمالية يتقدم الشعراء والفنانون لإبراز دلالاتها وغائيتها. يستهل محمد بن صالح القصائد المترجمة بمقدمة مسهبة وغنية يلقي فيها الكثير من الضوء على رؤية نيتشه للغة، لا بوصفها مجرد أداة للنقل والتوصل والابداع بل هي أداة للتقصي والاكتشاف ومعاناة في قول الذات والوجود. وإذا كان ذلك المفهوم للغة هو الذي شكل الرابط الحقيقي بين الشعر والفلسفة عند نيتشه، فإن خياره الشعري كما الفلسفي لم يذهب الى العقل البرهاني الأبولوني المتصل بالانساق والتحليلات المجردة، بل انحاز الى الجسد الديونيزي المتوتر والمنفعل باعتباره الوسيلة الأمثل للتحريض على الفعل كما على الابداع.', 'فريدريش فيلهيلم نيتشه (بالألمانية: Friedrich Nietzsche) ‏ (15 أكتوبر 1844 - 25 أغسطس 1900) فيلسوف ألماني، ناقد ثقافي، شاعر وملحن ولغوي وباحث في اللاتينية واليونانية. كان لعمله تأثير عميق على الفلسفة الغربية وتاريخ الفكر الحديث.\r\n\r\nبدأ حياته المهنية في دراسة فقه اللغة الكلاسيكي، قبل أن يتحول إلى الفلسفة. بعمر الرابعة والعشرين أصبح أستاذ كرسي اللغة في جامعة بازل في 1869، حتى استقال في عام 1879 بسبب المشاكل الصحية التي ابتلي بها معظم حياته، وأكمل العقد التالي من عمره في تأليف أهم كتبه. في عام 1889، وفي سن الرابعة والأربعين، عانى من انهيار وفقدان لكامل قواه العقلية. عاش سنواته الأخيرة في رعاية والدته وشقيقته، حتى توفي عام 1900.\r\n\r\n', 6, 'in stock', 'novels', 'unibookjo', 'OIP16.jfif', '', '2022-04-16 14:26:10'),
(9, 2, 1, 'دفاتر الورّاق', 'جلال برجس', 2020, 'تتكئ هذه الرواية على حكايات تُروى من خلال عدد من الدفاتر في إطار زمني يقع بين عامي 1947 و 2019 عن أشخاص يفقد بعضهم بيوتهم، ويعاني البعض الآخر أزمة مجهولي النسب، ويقاسي آخرون عدم انتمائهم إلى عائلات كبيرة. تتقاطع مصائر الشخصيات ببعضها فتبرز قيمة البيت والذي حمل رمز الوطن مقابل أكثر من شكل للخراب. الشخصية المحورية ورَّاق مثقف وقارئ نهم للروايات إلى درجة أن تتلبسه شخصية أي رواية تقنعه ويتصرف عبرها، لكن جراء العزلة والوحدة وما عاشه من قسوة في عالم صاخب، تتفاقم حالته النفسية فتكتمل إصابته بفصام الشخصية ليعيش صراعًا بين صوتين في داخله: واحد مُحرض على ارتكاب عدد من الجرائم حيال واقع لم يمنحه حقه في العيش، والثاني يقف بوجهه متكئًا على محمول معرفي عميق. تكشف الرواية إضافة إلى هذا المنحى وخلال التناوب بين محتوى الدفاتر من سينتصر على الآخر، وكيف تتشابك الحكايات ببعضها لتؤدي إلى مقولة رئيسية في الرواية مفادها: أن الخوف حتمًا سيؤدي إلى الخراب.', 'جلال برجس الغليلات (3 يونيو 1970 - ) شاعر وروائي أردني ولد في قرية حنينا في محافظة مادبا. تخرج من مدارس محافظة مادبا ثم درس هندسة الطيران وعمل في هذا المجال لسنوات، انتقل بعدها للعمل في الصحافة الأردنية كمحرر في صحيفة الأنباط، ومن ثم مراسلاً لصحيفة الدستور. وعضو هيئة تحرير عدد من المجلات. ثم عاد للعمل في مهنته الأولى.', 10, 'in stock', 'novels', 'unibookjo', 'OIP17.jfif', '', '2022-04-16 14:26:10'),
(10, 2, 1, 'ساق البامبو', 'سعود السنعوسي', 2012, '\r\nلماذا كان جلوسي تحت الشجرة يزعج أمي؟ أتراها كانت تخشى أن تنبت لي جذور تضرب في عمق الأرض ما يجعل عودتي إلى بلاد أبي أمراً مستحيلاً؟..ربما، ولكن، حتى الجذور لا تعني شيئاً أحياناً. لو كنت مثل نبتة البامبو.. لا انتماء لها.. نقتطع جزءاً من ساقها.. نغرسه، بلا جذور، في أي أرض.. لا يلبث الساق طويلاً حتى تنبت له جذور جديدة.. تنمو من جديد.. في أرض جديدة.. بلا ماض.. بلا ذاكرة.. لا يلتفت إلى اختلاف الناس حول تسميته.. كاوايان في الفلبين.. خيزران في الكويت.. أو بامبو في أماكن أخرى.', 'سعود السنعوسي (1981) كاتب وروائي كويتي، عضو رابطة الأدباء في الكويت. فاز عام 2013 بالجائزة العالمية للرواية العربية في دورتها السادسة عن روايته ساق البامبو التي حصدت جائزة الدولة التشجيعية في دولة الكويت عام 2012. وهي رواية تتناول موضوع العمالة الأجنبية في دول الخليج. وقد اختيرت من بين 133 رواية مقدمة لنيل الجائزة. وكتب السنعوسي في عدد من الصحف والمجلات الكويتية والخليجية، وانضم في عام 2018 لأسرة كتاب مجلة زهرة الخليج ينشر فيها مقالًا أسبوعيا.', 5, 'in stock', 'novels', 'unibookjo', '190px-ساق_البامبو_(رواية).jpg', 'y', '2022-04-16 14:26:10'),
(11, 2, 1, 'حوار مع صديقي الملحد', 'مصطفى محمود', 2013, 'لا شك أن هناك مجانسة بين بعض النفوس المجرمة وبين النار.. فبعض تلك النفوس هي في حقيقتها شعلة حسد وحقد وشهوة وغيرة وغل وضرام من الغضب والنقمة والثورة والمشاعر الإجرامية المحتدمة وأنها نار بالفعل.مثل تلك النفوس لا تستطيع أن تعيش في سلام .. لا تستطيع أن تحيا ساعة دون أن تشعل حولها حربا .. ودون أن تضرم حولها النيران .. لأن النيران هي بيئتها وطبيعتها. ومثل تلك النفوس يكون قرارها في النار هو الحكم العدل ويكون هذا المصير من قبيل وضع الشيء في مكانه.. فلو أنها أدخلت الجنة لما تذوقتها. ألم تكن ترفض السلام في الأرض ؟”', 'مُصطفى محمود (27 ديسمبر 1921 - 31 أكتوبر 2009)، هو فيلسوف وطبيب وكاتب مصري. ألف 89 كتاباً منها الكتب العلمية والدينية والفلسفية والاجتماعية والسياسية إضافة إلى الحكايات والمسرحيات وقصص الرحلات، ويتميز أسلوبه بالجاذبية مع العمق والبساطة. كان مصطفى محمود مقدّماً لأكثر من 400 حلقة من برنامجه التلفزيوني الشهير (العلم والإيمان). أنشأ عام 1979م مسجده في القاهرة المعروف بـاسم \"مسجد مصطفى محمود\". ويتبع له ثلاثة مراكز طبية تهتم بعلاج ذوي الدخل المحدود. وشكل قوافل للرحمة من ستة عشر طبيبًا، ويضم المركز أربعة مراصد فلكية، ومتحفاً للجيولوجيا، يقوم عليه أساتذة متخصصون. ويضم المتحف مجموعة من الصخور الجرانيتية، والفراشات المحنطة بأشكالها المتنوعة وبعض الكائنات البحرية. أُطلق على كويكب اسم (296753) مصطفى محمود تكريما له.', 7, 'in stock', 'novels', 'sanad books', 'OIP19.jfif', '', '2022-04-16 14:26:10'),
(12, 2, 1, 'سجين السماء', 'كارلوس زافون', 2011, 'بعد صدور \"ظل الريح\" و\"لعبة الملاك\" للروائي الإسباني كارلوس زافون، يكمل المترجم السوري معاوية عبد المجيد رحلته مع هذه الرباعية الروائية، التي تعرف بـ\"مقبرة الكتب المنسية\"، بإصداره الجزء الثالث \"سجين السماء\".\r\nيقول غلاف الرواية الصادرة حديثًا عن \"منشورات الجمل\": \"لعلّ القارئ الذي هام في ظلّ الريح، وتاه في لعبة الملاك، سيستغرب من دخوله زنزانة سجين السماء. إلّا أنّه سيتعرَّف باكرًا على لمسة كارلوس زافون وبراعته في تطويع مختلف التقنيّات السرديّة لِما يتوافق مع رؤيته. فإذا صوَّر لنا الكاتبُ مدينته برشلونة بين رومانسيّة الظلّ النوستالجيّة، ودوّامات اللعبة المتشابكة؛ فها هو في هذه المحطّة الثالثة، ينتقل بنا إلى عوالم السجين الداخليّة ليصف برشلونة ما تحت الأرض، برشلونة الخارجة من رهاب الحرب. لا شيء يحدث عن طريق الصدفة\".', 'كارلوس رويث زافون (بالإسبانية: Carlos Ruiz Zafón)‏‏ (25 سبتمبر 1964 – 19 يونيو 2020) روائي إسباني اشتهر بكتابته للسلسلة الروائية «مقبرة الكتب المنسية»، والتي تتألف من أربع روايات تُشكّل تيمة أدبية أساسية، ترتبط ببعضها عبر الشخصيات والمواضيع المتعددة، إلا أن كل رواية منها مستقلة عن الأخرى ومنفردة بذاتها.', 9, 'in stock', 'novels', 'sanad books', 'OIP20.jfif', '', '2022-04-16 14:26:10'),
(13, 2, 1, 'حديث الجنود', 'أيمن العتوم', 2014, 'إنها الكلمة وإنها الثورة، وإنها نحن نشكل حروفها على وهج الحق فيولي الباطل، وعلى فيء العدل فينحسر الظلم !!\r\n \r\n\r\nمفتاح الثورة كلمة، وتصنع الثورة كلمة: (العدو من أماكم والبحر من ورائكم)، وأول الرسالة كلمة:(اقرأ)، وأول الرحمة كلمة: (كوني برداً وسلاما)، واعظم العذاب كلمة: (اخسؤوا فيها ولا تكلمون)، وأشد الحسرة كلمة: (سلام عليكِ ... سلام لا لقاء بعده)، وتهوي بالعالين الراتعين في نعيمهم كلمة: )اهبطوا منها جميعاً)، وتطيح بالأصنام كلمة: (وقل جاء الحق وزهق الباطل إن الباطل كان زهوقا)، وتوطد أركان الدولة كلمة: )إني لأرى رؤوساً قد أينعت)، وتفك أسر العاني كلمة: )اذهبوا فأنتم الطلقاء)، وتنفذ كالسهم إلى الروح كلمة: )أشد عليهم من وقع النبل)، وتصنع الوجود من العدم كلمة: (كن فيكون).', 'أيمن العتوم شاعر وروائي أردنيّ (ولد في الأردن - جرش سوف 2 آذار 1972)، تلقّى تعليمه الثانوي في دولة الإمارات العربية المتحدة - إمارة عجمان والتحق بـجامعة العلوم والتكنولوجيا الأردنية ليتحصل على بكالوريوس الهندسة المدنية فيها عام 1997 وفي عام 1999 تخرّج في جامعة اليرموك بشهادة بكالوريوس لغة عربية، ثمّ التحق بالجامعة الأردنية ليُكمل مرحلة الدراسات العليا في اللغة العربية وحصل على شهادتي الماجستير والدكتوراة في اللغة العربية تخصص نَحو ولغة عامي 2004 و2007، اشتُهر بروايته يا صاحبي السجن التي صدرت عام 2012 وتعبّر عن تجربة شخصيّة للكاتب في السجون الأردنية خلال عامَي 1996 و 1997 بكونه معتقلاً سياسياً. كما له دواوين شعريّة عديدة أحدثها ديوان \"طيور القدس عام 2016\".', 11, 'in stock', 'novels', 'Ayman Otoom', 'Untitled-1-Recovered_270x.png', '', '2022-04-16 14:26:10'),
(14, 2, 1, 'حدّثهم عن المعارك\r\nوعن الملوك وعن الفيَلة', 'ماتياس إينار', 2022, 'في العام 1506 دُعى النحات الشاب مايكل أنجلو من قبل سلطان القسطنطينية كي يصمم جسرًا. وقد وعده السلطان بتخليد ذكراه بالإضافة إلى مكافأة مادية ضخمة. يقبل مايكل أنجلو بهذا العرض ويسافر إلى القسطنطينية حيث ينبهر بجمال تفاصيل عاصمة الإمبراطور العثمانية ويقوم بتصوير وتدوين انطباعاته عن هذا المكان. \r\n', 'وُلد ماتياس إينار في نيور في 11 يناير عام 1972 ، وهو كاتب ومُترجم فرنسي . وقد\r\n درس في معهد اللغات و الحضارات الشرقية بباريس زهي دروس عربية وفارسية . وأقام في الشرق واستقر في برشلونة عام 2000.', 7, 'in stock', 'novels', 'unibookjo', '272907025_4928912677131684_2109997680288671133_n.jpg', '', '2022-04-16 14:26:10'),
(15, 2, 1, 'جميع نقود جدتي', 'أولف نيلسون', 2012, 'في يوم من الأيام، اختلط عقل جدتي، وأصبحت عدائية. كنت في السادسة من عمري ، وكانت أحيانًا تقوم برعايتي خلال النهار. كانت تعيش في قرية خارج المدينة. كان اليوم يوم خميس ، وكنت انتظر، فرحت أذرع الحديقة ذهابًا و جيئة .', 'ولف نيلسون (بالسويدية: Ulf Nilsson)‏ هو كاتب للأطفال وكاتب سويدي، ولد في 1948 في هلسينغبورغ في السويد.', 12, 'in stock', 'novels', 'جملون', '1023198_170x170.jpg', 'y', '2022-04-16 14:26:10'),
(16, 2, 1, 'جئت متأخرا', 'علي الحديثي', 2016, 'لا تفشل!.. أنت الوحيد الذي بإمكانه أن يقرّر ذلك، قُلْ لن أفشل.. ولن تفشل أبدًا. صحيح أن البؤس قد يولد توأمًا للإنسان، لكنّ عين الحياة تبقى كليلةً عن هذي المقادير، مفتوحةً وِسْعَ الشمس على الأمل والإرادة، وإلّا.. كيف صار اليتيم نبيًّا، والكفيف فيلسوفًا، والأعرج قائدًا؟!.. ليس ثمّة شيء - في البداية- اسمه حظّ، ليست قلّة الحظّ أيضًا مجرّد شمّاعة نعلّق عليها فشلنا، لكن بين هذا وذاك يوجد شيء اسمه \"ثورة\" على قلّة الحظّ، ومثابرةٌ على زيادة فُرصه، أنتَ تصنع حظّك، وأنت الذي تجعل \"النحس\" رفيقك. ولن يفيدك أبدًا.. أن تأتي متأخّرًا..', 'علي الحديثي (1968) هو كاتب عراقي مواليد مدينة بغداد. حاصل على البكالوريوس في اللغة العربية، يعمل مدرسًا للغة العربية. هو عضو اتحاد الأدباء والكتاب العراقيين، وعضو نقابة الصحفيين. بدأ كتابة القصة القصيرة منذ عام 1995، فازت قصته القصيرة (عندما يسكت زوربا) بالمركز الأول في مسابقة دار ضاد للطباعة والتوزيع في مصرعام 2015، وفازت روايته (ارفعوا صوت التلفاز) بالمركز الأول مناصفة في مسابقة رابط الشعراء العرب للرواية العربية في عام 2017.', 12, 'in stock', 'novels', 'sanad books', 'OIP21.jfif', '', '2022-04-16 14:26:10'),
(17, 2, 1, 'ثلج الربيع', 'يوكيو ميشيما', 2017, '\"ثلج الربيع\" هي الحركة الأولى في سيمفونية \"بحر الخصب\" للروائي الياباني \"يوكيو ميشيما\"، لتتابع الحركات الثانية الثالثة والرابعة \"الجياد الهاربة\" و\"معبر الفجر\" و\"سقوط الملاك\" لتشكل من رباعية \"بحر الخصب\" عملاً صاف وممتد، الذي من أكثر الأعمال الأدبية جرأة', 'يوكيو ميشيما (يابانية: 三島 由紀夫) هو الاسم الأدبي للكاتب الياباني كيميتاكي هيراوكا (باليابانية: 平岡 公威) (و.14 يناير 1925 ـ ت. 25 نوفمبر 1970)، الذي كان روائياً وشاعراً وكاتباً مسرحياً وممثلاً ومخرج أفلام. رشح ميشيما للحصول على جائزة نوبل في الأدب ثلاث مرات، وكان اسمه معروفاً على نطاق عالمي، ويعد من أشهر الكتاب اليابانيين في القرن العشرين، وقد مزجت أعماله الطليعية بين القيم الجمالية الحديثة والتقليدية وحطمت الحواجز الثقافية وكانت الجنسانية والموت والتحول السياسي من أهم محاورها.\r\n\r\n', 11, 'in stock', 'novels', 'sanad books', 'OIP23.jfif', '', '2022-04-16 14:26:10'),
(18, 2, 1, 'ثلاثية غرناطة', 'رضوى عاشور', 2009, 'ثلاثية غرناطة هي ثلاثية روائية تتكون من ثلاث روايات للكاتبة المصرية رضوى عاشور و هم على التوالي: غرناطة - مريمة - الرحيل. وتدور الأحداث في مملكة غرناطة بعد سقوط جميع الممالك الإسلامية في الأندلس، و تبدأ أحداث الثلاثية في عام 1491م، وهو العام الذي سقطت فيه غرناطة بإعلان المعاهدة التي تنازل بمقتضاها (أبو عبد الله محمد الصغير) آخر ملوك غرناطة عن ملكه لملكي قشتالة وأراجون، وتنتهى بمخالفة آخر أبطالها الأحياء (عليّ) لقرار ترحيل المسلمين حينما يكتشف أن الموت في الرحيل عن الأندلس و ليس في البقاء.', 'رضوى عاشور (26 مايو 1946 - 30 نوفمبر 2014) قاصة وروائية وناقدة أدبية وأستاذة جامعية مصرية،\r\n\r\nزوجة الأديب الفلسطيني مريد البرغوثي، ووالدة الشاعر تميم البرغوثي  توفيت يوم 30 نوفمبر 2014.\r\n\r\nتميز مشروعها الأدبي، في شقه الإبداعي، بتيمات التحرر الوطني والإنساني، إضافة للرواية التاريخية. تراوحت أعمالها النقدية، المنشورة بالعربية والإنجليزية، بين الإنتاج النظري والأعمال المرتبطة بتجارب أدبية معينة. تُرجمت بعض أعمالها الإبداعية إلى الإنجليزية والإسبانية والإيطالية والإندونيسية.', 10, 'in stock', 'novels', 'sanad books', 'OIP24.jfif', '', '2022-04-16 14:26:10'),
(19, 2, 1, 'تسعة عشر', 'أيمن العتوم', 2018, 'كنت أشعر دائمًا أَنّ بابًا يُفضي إلى مكتبة من خلفه، ليس بابًا عاديًا، إنّه بابٌ يفتح على المُطلَق، وعلى الحياة الأخرى الأكثر إدهاشًا وغموضًا وسحرًا. إنّه بابٌ يفصل بين حياتين، بين حياة تافهة ساذجة، وبين حياة جادّة نابهة. لكأنّ الباب هو البرزخ بين هاتين الحياتين، وعليه فإنّه من اللائق أن تخلع عنك تفاهتك قبل أنْ تخطو الخُطوة الأولى عبر هذه البوّابة، وتلبس لِباس الرّهبان المقيمين في حضرة الصلوات الطاهرات..', 'أيمن العتوم شاعر وروائي أردنيّ (ولد في الأردن - جرش سوف 2 آذار 1972)، تلقّى تعليمه الثانوي في دولة الإمارات العربية المتحدة - إمارة عجمان والتحق بـجامعة العلوم والتكنولوجيا الأردنية ليتحصل على بكالوريوس الهندسة المدنية فيها عام 1997 وفي عام 1999 تخرّج في جامعة اليرموك بشهادة بكالوريوس لغة عربية، ثمّ التحق بالجامعة الأردنية ليُكمل مرحلة الدراسات العليا في اللغة العربية وحصل على شهادتي الماجستير والدكتوراة في اللغة العربية تخصص نَحو ولغة عامي 2004 و2007، اشتُهر بروايته يا صاحبي السجن التي صدرت عام 2012 وتعبّر عن تجربة شخصيّة للكاتب في السجون الأردنية خلال عامَي 1996 و 1997 بكونه معتقلاً سياسياً. كما له دواوين شعريّة عديدة أحدثها ديوان \"طيور القدس عام 2016\".', 12, 'in stock', 'novels', 'Ayman Otoom', '414064b2c766e5ecc99d111107603137_270x.jpg', '', '2022-04-16 14:26:10'),
(20, 2, 1, 'تراب الماس', 'أحمد مراد', 2010, 'لم يكن \"طه\" سوى مندوب دعاية طبية في شركة أدوية ؛ حياة باهتة رتيبة ، بدلة كرافتة و حقيبة جلدية و لسان لبق يستميل أعتى الأطباء لأدويته .. كان ذلك قبل أن يسقط .. جريمة قتل غامضة تتركه خلفها و قد تبدل عالمه .. للأبد .. تتحول حياته إلى جزيرة من الأسرار, يبدأ اكتشافها في دفتر عتيق يعثر عليه مصادفة, و يجد معه أداة رهيبة لها فعل السحر .. سنقرأ كيف تتحول هذه الجريمة إلى سلسلة من عمليات القتل. وكيف يصبح القتل بابًا يكشف لنا عالماً من الفساد ، وسطوة السلطة التي تمتد لأجيال في تتابع مثير لا يؤكد أيا', 'حمد مراد (مواليد القاهرة 1978) كاتب وسيناريست ومصور ومصمم جرافيك مصري، تخرّج في مدرسة ليسيه الحرّية بباب اللوق عام 1996 قبل أن يلتحق بالمعهد العالي للسينما ليدرس التصوير السينمائي، وتخرّج عام 2001 بترتيب الأول على القسم، ونالت أفلام تخرّجه \"الهائمون - الثلاث ورقات - وفي اليوم السابع\" جوائز للأفلام القصيرة في مهرجانات بإنجلترا وفرنسا وأوكرانيا.\r\n\r\n', 11, 'in stock', 'novels', 'sanad books', 'OIP25.jfif', 'y', '2022-04-16 14:26:10'),
(21, 2, 1, 'خاوية', 'أيمن العتوم', 2016, 'جاءت خاوية على ثلاثة اقسام ، عالج القسم الاول قضية التوحد عند الاطفال ، وما يلحقه هذا المرض من شقاء على الوالدين وحتى لو حاء هذا الطفل بعد محاولاتهما المستميتة للحصول عليه.\r\n\r\nتحدث القسم الثاني عن الحياة اليومية التي يعيشها السوريون منذ بدء الحرب في سوريا وحتى يومنا هذا,دارت احداث هذا القسم في سوريا، وبدأت بقصة حب لطيفة بين شاب وفتاة اختارا ان يكملا حياتهما معا، الا ان الحرب كانت اقسى من ان تتحقق هذه الامنية,ليتفرق كل منهما ويواجه مصيرا ابشع من خلافاتهما الصغيرة التي فاضت بها حياتهما اليومية حتى اختنق كل منهما بالآخر.\r\n\r\n', 'أيمن العتوم شاعر وروائي أردنيّ (ولد في الأردن - جرش سوف 2 آذار 1972)، تلقّى تعليمه الثانوي في دولة الإمارات العربية المتحدة - إمارة عجمان والتحق بـجامعة العلوم والتكنولوجيا الأردنية ليتحصل على بكالوريوس الهندسة المدنية فيها عام 1997 وفي عام 1999 تخرّج في جامعة اليرموك بشهادة بكالوريوس لغة عربية، ثمّ التحق بالجامعة الأردنية ليُكمل مرحلة الدراسات العليا في اللغة العربية وحصل على شهادتي الماجستير والدكتوراة في اللغة العربية تخصص نَحو ولغة عامي 2004 و2007، اشتُهر بروايته يا صاحبي السجن التي صدرت عام 2012 وتعبّر عن تجربة شخصيّة للكاتب في السجون الأردنية خلال عامَي 1996 و 1997 بكونه معتقلاً سياسياً. كما له دواوين شعريّة عديدة أحدثها ديوان \"طيور القدس عام 2016\".', 9, 'in stock', 'novels', 'Ayman Otoom', '55599870-b1d5-4270-ad49-851315ea5620_270x.png', '', '2022-04-16 14:26:10'),
(22, 2, 1, 'ظل الريح', 'كارلوس زافون', 2001, 'رواية لا تقاوم. حصلت في زمن قصير على ثناء شامل في كل العالم. وتضمنت من الأسرار والخفايا ما جعلها مُغوية مثل دمى الماتريوشكا الروسية.\r\n- لافيجارو\r\n\r\nاستطاع كارلوس زافون أن يجمع بين غارثيا ماركيز وأمبرتو إيكو وخورخي لويس بورخيس في مشهد ساحر ومعقد ببراعة ثاقبة وكتابة عجيبة.\r\n- نيويورك تايمز\r\n\r\nمن الصعب أن يعثر القارئ على رواية تحتوي على هذا القدر من العواطف والمآسي والإثارة مثل رواية \"ظل الريح\" للعملاق\r\n-كارلوس رويث زافون\r\nستقرأ الرواية في جلسة واحدة ولن تنام الليل وأنت تتعقّب ظل الريح .\r\n', 'كارلوس رويث زافون (بالإسبانية: Carlos Ruiz Zafón)‏‏ (25 سبتمبر 1964 – 19 يونيو 2020) روائي إسباني اشتهر بكتابته للسلسلة الروائية «مقبرة الكتب المنسية»، والتي تتألف من أربع روايات تُشكّل تيمة أدبية أساسية، ترتبط ببعضها عبر الشخصيات والمواضيع المتعددة، إلا أن كل رواية منها مستقلة عن الأخرى ومنفردة بذاتها.', 9, 'in stock', 'novels', 'sanad books', 'OIP27.jfif', '', '2022-04-16 14:26:10'),
(23, 2, 1, 'في عالم آخر', 'أديبة المقبل', 2019, 'طالب جامعي يدعى فيصل مبتعث لأمريكا أصيب أخيه الصغير بفشل كلوي وأظهرت نتائج التحليلات أنه المتطابق الوحيد مع أخيه في أثناء هذا يقترح عليه أحد أصدقائه السفر معه لزيارة دولته ( ميانمار) فيوافق وبعد وصوله يتورط بحادثة قتل ويفقد جوازه فيعيش مع الروهنجيا ويتورط في بعض مواقفهم أثناء محاولته استعادة جواز سفره.', '', 11, 'in stock', 'novels', 'جملون', 'OIP28.jfif', '', '2022-04-16 14:26:10'),
(24, 2, 1, 'فجر السايرينات', 'أسامة المسلم', 2021, ' نجني الكثير حينما نجتهد ونطمح .. ونخسر كل ما جنيناه بعد ما نتمادى ونطمع .. المكاسب لا قيمة لها عندما تكون فيضاً جَلَبه هوس .. والتنازل عن بعضها فصدٌ لا مناص منه للبقاء والاستمرار ..\" أسامة المسلم \"الكتاب في حالة الطلب المسبق، سيتم شحنه فور صدوره\"', 'سامة المِسّلِم كاتب وروائي سعودي من المنطقة الشرقية، في الأحساء من مواليد (15 ربيع الأول 1397هـ / 5 مارس 1977م) برز في وقت متأخر من حياته الأدبية في كتابة الروايات من نوع الفانتازيا والفانتازيا التاريخية  بقصص وأحداث ذات طابع تشويقي وبسرد الأحداث على الطريقة السينمائية ذات الحوارات المتينة بأسلوب السهل الممتنع، ويسرد من خلالها الأحداث بشكل متقطع مع وضع أسماء فريدة وغريبة من نوعها ويكمن في معظم رواياته النهايات الغير متوقعة. وهذا الشيء الذي جعل بعضًا من رواياته ترشح لكي تصبح فلمًا لكنه رفض وصرح بقوله (\"أنه لم يجد بعد العرض الذي لا يستطيع رفضه\"). وقد تُرجمت بعضًا من رواياته للإنجليزية كرواية (خوف وبساتين عربستان والجزء الأول من لج) في الأجزاء الأولى، كما وأن له رواية واحدة مشتركة مع الكاتب الكويتي \"عبد الوهاب الرفاعي\" بعنوان \"مخطوطات مدفونة\".', 10, 'in stock', 'novels', 'sanad books', 'OIP29.jfif', '', '2022-04-16 14:26:10'),
(25, 2, 1, 'غسق الاوثان', 'فريدريك نيتشه', 2010, '                                                                                                                                                                                                              \r\nالرجال الذين سيولدون بعد الممات -أنا على سبيل المثال- سيسوء فهمهم أكثر من المطابقين لعصرهم، لكنه سيُستمع إليهم بصفة أفضل. ولنقلها بأكثر صرامة: لن يكتب لنا أن نُفهم البتة؛ -من هنا تكون سلطتنا                                                                                                                                                         ', '                                                                                                         فريدريش فيلهيلم نيتشه (بالألمانية: Friedrich Nietzsche) ‏ (15 أكتوبر 1844 - 25 أغسطس 1900) فيلسوف ألماني، ناقد ثقافي، شاعر وملحن ولغوي وباحث في اللاتينية واليونانية. كان لعمله تأثير عميق على الفلسفة الغربية وتاريخ الفكر الحديث\r\nبدأ حياته المهنية في دراسة فقه اللغة الكلاسيكي، قبل أن يتحول إلى الفلسفة. بعمر الرابعة والعشرين أصبح أستاذ كرسي اللغة في جامعة بازل في 1869، حتى استقال في عام 1879 بسبب المشاكل الصحية التي ابتلي بها معظم حياته، وأكمل العقد التالي من عمره في تأليف أهم كتبه. في عام 1889، وفي سن الرابعة والأربعين، عانى من انهيار وفقدان لكامل قواه العقلية. عاش سنواته الأخيرة في رعاية والدته وشقيقته، حتى توفي عام 1900.                                                                                                                                                                                                ', 6, 'out of stock', 'novels', 'unibookjo', 'philosophy-book-1.jpg', '', '2022-04-16 14:26:10'),
(26, 2, 1, 'عين الشمس', ' إبتسام إبراهيم تريسي.', 2010, 'تروي الكاتبة السورية \"ابتسام إبراهيم تريسي\" في هذه الرواية حكاية بطلتها \"نسمة\"، التي تعود إلى سوريا بعد سنوات من المنفى في السويد، لتضطرّ إلى مواجهة ذكريات أليمة؛ من المعاناة العائلية في ظل والد قاس ومتناقض، إلى الأحداث السياسية التي تؤثر على عائلتها وعلى جميع أهل حلب. تستكشف الرواية كذلك علاقات \"نسمة\" بالرجال الذين مرّوا في حياتها، من والدها وأخيها، مرورًا بعشّاقها والرجل الذي يعذّبها، وصولًا إلى زوجها.', 'ابتسام إبراهيم تريسي (و. 1959 م) هي روائية سورية.', 9, 'in stock', 'novels', 'جملون', 'عين الشمس.jpg', '', '2022-04-16 14:26:10'),
(27, 2, 1, 'عندما التقيت عمر بن الخطاب', 'ادهم شرقاوي', 2017, 'كان في السَّادسة والعشرين عندما أصابته دعوة النبيّ صلى الله عليه وسلم في قلبه اللهم أعزّ الإسلام بأحبِّن عمر بن الخطاب أو عمرو بن ه شام!هكذا بدأت الحكاية ، دعوةٌ جذبته من ياقة كفره إلى نور الإسلام', 'دهم شرقاوي كاتب فلسطيني ولد ونشأ في مدينة صور اللبنانية حاصل على دبلوم تربية رياضية من كلية التربية وإجازة وماجستير في الأدب العربي من الجامعة اللبنانية في بيروت. عمل في صحيفة الوطن القطرية بدأ بالكتابة عبر منصة منتدى الساخر ثم أصدر أول كتاب له عام 2012 بعنوان أحاديث الصباح، ينشر مؤلفاته تحت اسم مستعار «قس بن ساعدة»', 10, 'in stock', 'novels', 'sanad books', 'OIP32.jfif', '', '2022-04-16 14:26:10'),
(28, 2, 1, 'نجع بريطانيا العُظمى', 'حسام العادلي', 2021, '“نجع بريطانيا العُظمى” عنوان لافت، فالنجع هو نجع السعداوية بالصعيد فمن أين جاء الكاتب العنوان؟ هنا تدخل في الرواية لتكتشف أنك أمام تاريخ يتمدد إلى الخلف لبدايات القرن العشرين وصراع عائلة السعداوية للفوز بالعُمُدية ثم خسارتها ولا ينفصل ذلك كله عن الوجود البريطاني والحرب العالمية الأولى والثانية حتى ثورة يوليو وما بعدها إلى 1956. كل ذلك التاريخ يتهادى ويتواتر وينفجر مع زيارة زين سليل السعداوية إلى عمه حسنين الذي صار تاجرًا كبيرًا في روض الفرج بعد أن صار كل شيء ينتمي إلى العهد البائد بعد ثورة يوليو. على ناحية، السعداوية وأعداؤها وأصدقاؤها، وعلى ناحية، مستر هاريس الإنجليزي وأصوله وزوجته وبنته، يتداخلون من أبواب السياسة والجنس والقتل والحرب وغيرها بشكل مذهل. لا يختلف الصعود هنا عنه هناك ولا النهايات. ليس بتداخل الحكايات فقط لكن بالرؤية للإنسان بين طموحه ونزواته وجرائمه فلا فرق. النجع واحد هنا أو هناك. فارقٌ أن الكتابة جعلت المكان رايتها في كل الحكي والصور بشكل مذهل عن الجنس والخيانات والاغتصاب والقتل والحب بلغة بنت المكان بأشجاره ومائه ونباتاته وطميه ورماله وعاداته. لغة من يرى.. لا من يحكي وهذا هو أجمل إنجاز للفن. هذا هو ما يجعلك تقرأ بانبهار ولا تتوقف.', 'حسام العادلي (2 أغسطس 1982 - 13 شوال 1402 هـ ) قاضي، وروائي مصري، تخرّج في ليسانس الحقوق في عام 2003 جامعة أسيوط . صدر له مجموعة قصصية عام 2014 بعنوان لمحات، ورواية «أيام الخريف» في عام 2018 عن الدار المصرية اللبنانية .وعن نفس الدار صدرت أحدث رواياته «نجع بريطانيا العظمى» في عام 2021. يكتب مقالات الرأي في جريدة المصري اليوم , وله عشرات المقالات في جريدة الأخبار, البوابة، ومجلة الهلال والبوابة الإلكترونية لمؤسسة الهلال الصحفية، ومجلة عالم الكتاب .', 5, 'in stock', 'novels', 'Amazon', 'OIP33.jfif', '', '2022-04-16 14:26:10'),
(29, 2, 1, 'عن الرجال والبنادق', 'غسان كنفاني', 1963, 'مجموعة قصصية يستلهم فيها كنفاني ككل إبداعه الأدبي مأساة شعب فلسطين الذي لم يكتب غسان كنفاني شيئا إلا عنه، ولم يستلهم قصصه إلا منه. عن الرجال والبنادق مجموعة قصص لغسان كنفاني تتحدث حول كفاح الشعب الفلسطيني. تكمن القيمة الأساسية لهذا الكتاب في مسألتين: القيمة التاريخية: حيث نكتشف في كنفاني، إرادة البحث لا تنتهي، والتي تدفعه إلى البحث الجدي عن أساليب جديدة، بشكل دائم. القيمة النضالية: فنكتشف مع كنفاني، كيف جرى تأسيس دراسة جديدة في الأدب الفلسطيني، بل والأدب العربي، هي محاولة الكتابة نصاً للواقع المتحرك، وعلامة ثورية. صدرت هذه المطبوعة ضمن سلسلة أعمال غسان كنفاني.', 'غسان كنفاني (عكا 9 أبريل 1936 - بيروت 8 يوليو 1972) هو روائي وقاص وصحفي فلسطيني، ويعتبر غسان كنفاني أحد أشهر الكتاب والصحافيين العرب في القرن العشرين. فقد كانت أعماله الأدبية من روايات وقصص قصيرة متجذرة في عمق الثقافة العربية والفلسطينية. \r\n\r\nولد في عكا، شمال فلسطين، في التاسع من نيسان عام 1936م، وعاش في يافا حتى أيار 1948 حين أجبر على اللجوء مع عائلته في بادئ الأمر إلى لبنان ثم إلى سوريا. عاش وعمل في دمشق ثم في الكويت وبعد ذلك في بيروت منذ 1960 وفي تموز 1972، استشهد في بيروت مع ابنة أخته لميس في انفجار سيارة مفخخة على أيدي عملاء إسرائيليين.\r\n\r\n', 6, 'in stock', 'novels', 'sanad books', 'OIP35.jfif', '', '2022-04-16 14:26:10'),
(30, 2, 1, 'عزازيل', 'د.يوسف زيدان', 2008, 'حيث تتحدث الرواية عن حياة راهب مسيحيّ بكل تفاصيلها، وتناقش ما يتجنبه البعض ويخشاه خوفاً من الدخول بمتاهات الصراعات الدينيّة، كما تُسلط الضوء على بعض الفترات الدينيّة التي كانت شبه مجهولة، أو متجاهلة، وتؤرخها بأسلوب رائع، وتصوير مكانيّ وزمانيّ ممتع للغاية\r\n\r\n', 'يوسف محمد أحمد زيدان أستاذ جامعي كاتب وفيلسوف مصري، ومتخصص في التراث العربي المخطوط وعلومه، له عدة مؤلفات وأبحاث علمية في الفكر الإسلامي والتصوف وتاريخ الطب العربي، وله إسهام أدبي في أعمال روائية منشورة، وله مقالات دورية وغير دورية في عدد من الصحف العربية. عمل مديراً لمركز المخطوطات بالإسكندرية في مكتبة الإسكندرية.', 6, 'in stock', 'novels', 'sanad books', 'عزازيل.jpg', 'y', '2022-04-16 14:26:10'),
(31, 2, 1, 'زورو  وتبدأ الأسطورة', 'ايزابيل الليندي', 2007, 'مغامر، متولع، مقدام ولعوب. إنها أسطورة الثعلب \"زورو\". وهي قصة حياة رائعة في أزمنة استثنائية، مكتوبة بريشة حادة وسخرية ناعمة: إنها حياة دييغو دلابغا وقد نزع قناعه. حكاية تبدأ في العام 1970، في أراضي كاليفورنيا العليا، حين يعشق نقيب أسباني شاب فتاة هندية متمردة الروح.\r\n\r\n', 'إيزابيل الليندي يونا (بالإسبانية: Isabel Allende Llona)‏ روائية تشيلية وُلدت في 2 أغسطس 1942، وحاصلة على العديد من الجوائز الأدبية المهمة، ومن الأسماء المرشحة دائماً للحصول على جائزة نوبل. تُصنف كتاباتها في إطار الواقعية السحرية، وتنشط في مجال حقوق المرأة والتحرر العالمي. من أهم رواياتها: بيت الأرواح، وإيفالونا، وما وراء الشتاء.', 11, 'in stock', 'novels', 'جملون', 'OIP38.jfif', '', '2022-04-16 14:26:10'),
(32, 2, 1, 'ظلام مرئي', ' وليام ستايرون', 1989, 'أحس الروائي الأمريكي الشهير «وليام ستايرون» بالشلل، من جراء مشاعر السخط، والاستياء، واللامبالاة، واليأس، وفقدان القدرة على الكلام والمشي، التي تملَّكته حينما سقط في قبضة اكتئاب متقدم، ابتلع حياته وتركه على حافة الانتحار.\r\nفي هذه المذكرات المبهرة، يصف الروائي الكبير هبوطه المدمِّر في الكآبة، فيأخذنا في رحلة غير مسبوقة إلى عالم الجنون، مليئة بتأملات كاشفة لمرضٍ يصيب الملايين، لكن ما زال يُساء فهمه على نطاق واسع. هذا النص المؤثر والشجاع.\r\n«تقشعر له الأبدان، لكنه مفعم بالأمل... منير... ساحر»\r\n', 'ويليام كلارك ستايرون (11 يونيو 1925 - 1 نوفمبر 2006) (بالإنجليزية: William Clark Styron Jr)‏ روائي وكاتب مقالات أمريكي، فاز بجوائز أدبية كبرى عن أعماله.\r\n\r\nسنة 1985، عانى من نوبة اكتئاب حادّة. بمجرد تعافيه من مرضه، تمكن ستايرون من كتابة مذكراته المعنونة بظلام مرئي (1990)، وهو العمل الذي اشتهر به خلال العقدين الأخيرين من حياته.', 7, 'in stock', 'novels', 'unibookjo', 'OIP40.jfif', '', '2022-04-16 14:26:10'),
(33, 2, 1, 'الواحة الخفية', 'بول سوسمان', 2009, 'بعد نجاحه في \"آخر أسرار الهيكل\" و\"جيش قمبيز المفقود\" يعود الروائي \"پول سوسمان\" إلينا برواية مثيرة تمثل إحدى قصص الماضي غموضاً، لتنسحب بأحداثها إلى الحاضر. أما مسرح الأحداث فيبدأ في العام 2135 قبل الميلاد في الصحراء الغربية. من هنا بدأت قصة \"الواحة الخفية The Hidden Oasis\". وبالتحديد عند قاعدة الجرف الصخري عندما نطق \"إمتي\" المبجل تعويذتي الإغلاق والإخفاق. على أن تظلا ساريتين حتى تسود أيام أفضل وأكثر استقراراً. وفي العام 1986 وفي الصحراء الغربية أيضاً سقطت طائرة أنتونوف محملة بمواد يفترض أنها بالغة الخطورة كانت متوجهة إلى إحدى دولتين متحاربتين في ذلك الحين في الشرق الأوسط. واختفى أثرها. حاولت السي آي أيه تقصي أثر الطائرة منذ ذلك الحين عبر عمل استخباراتي من خلف الستار. ولكن عندما يجتمع فساد البعض في الدول العظمى يتحول تقفي الأثر إلى معانٍ وأهداف مغايرة، حيث ستكون النتائج بالطبع مغايرة. إلا أن موت ألكس عالمة الآثار في مصر وقدوم شقيقتها فريا لحضور مراسم دفنها وتعرفها إلى رفاق شقيقتها وخصوصاً فلين برودي وهو عالم آثار وعميل سابق في جهاز الاستخبارات البريطانية. والشكوك التي ستراود فريا حول موت شقيقتها، ستفتح آفاقاً جديدة في تاريخ واحة زرزورة وربما في مستقبلها. مما سيضفي فهماً جديداً إلى ما حدث وسيحدث. يعرض بول سوسمان في رائعته هذه النتائج المدمرة لالتقاء المصالح بين جشع رجل الأعمال المصري جرجس والسي آي أيه وستكون الحصيلة قتلاً، وتدميراً، ومطاردات تقطع الأنفاس، إلا أن أذى الأشرار لا حدود له. وسيبلغ السيل الزبى عندما توشك أقدس أقداس \"إمتي\" المبجل على الانتهاك. وعلى أي حال سيكون لبدو الصحراء وشهامتهم ودرايتهم دور مميز. يقودنا پول سوسمان بإلمامه الكبير بمصر الحاضر والتاريخ عبر صفحات هذه الرواية في وصف قل نظيره وبمشهدية سينمائية رائعة. فهل ستحصل المأساة نتيجة عبث الإنسان بما لا يجب العبث به؟', 'ول سوسمان (بالإنجليزية: Paul Sussman)‏ (21 يوليو 1966 - 31 مايو 2012) كان مؤلفا وعالم آثار وصحفي بريطاني، ترجمت رواياته إلى أكثر من 33 لغة حول العالم. تدور روياته عن أحداث  في مصر.', 15, 'in stock', 'novels', 'sanad books', 'OIP41.jfif', '', '2022-04-16 14:26:10'),
(34, 2, 1, 'طفل روزماري', 'إيرا ليفين', 1967, 'بالرغم من تاريخ مبنى برامفورد الأسود المليء بالسحرة والسفاحين وحوادث الانتحار المُريبة، تُقرّر روزماري وزوجها الممثل المغمور جاي وودهاوس الانتقال للعيش في شقة فاخرة فيه. تتوق روزماري لإنجاب الأطفال رغم معارضة زوجها، لكن كل شيء ينقلب بعد دخول الجيران الغامضين الفضوليين إلى حياتهما. وتكتشف روزماري المؤامرة التي تُنسج حولها، وحول جنينها الذي لم يولد بعد.', 'يرا ليفين (بالإنجليزية: Ira Levin)‏ (27 أغسطس 1929، ذا برونكس في الولايات المتحدة - 12 نوفمبر 2007، مانهاتن في الولايات المتحدة)؛ كاتِب ومؤلِّف، كاتب مسرحي، شاعر غنائي، كاتب سيناريو وروائي أمريكي. درس في جامعة نيويورك.', 8, 'in stock', 'novels', 'sanad books', 'طفل روزماري.jpg', '', '2022-04-16 14:26:10'),
(35, 2, 1, 'طريق جهنم', 'أيمن العتوم', 2019, 'الأمل ليس وهمًا كما يعتقد اليائس ، الأمل حالة ؛ انظر حولك وستجد كل شئ يحتفي بالأمل. كل شئ يتحول إليه. كل شئ يريد أن يكونه. تخيل أن الكون والكائنات ب لا أمل ، كيف يمكن أن تكون هناك حياة ، كيف يمكن أن يُعبد الله ؟! الآخرة أمل الدنيا. الفوز أمل المعذبين. النهاية أمل المتعبين. الحقيقة أمل الخائفين. والعدل أمل المظلومين.   الرواية تنقسم بينَ بطلَين، الطّاغية الّذي يتكلّم بلسانه، والسّجين الّذي يروي مشاهداته خلال ثلاثين عامًا من العذابات الّتي لا يُمكن أنْ يتخيّلها أيّ بشريّ.', 'أيمن العتوم شاعر وروائي أردنيّ (ولد في الأردن - جرش سوف 2 آذار 1972)، تلقّى تعليمه الثانوي في دولة الإمارات العربية المتحدة - إمارة عجمان والتحق بـجامعة العلوم والتكنولوجيا الأردنية ليتحصل على بكالوريوس الهندسة المدنية فيها عام 1997 وفي عام 1999 تخرّج في جامعة اليرموك بشهادة بكالوريوس لغة عربية، ثمّ التحق بالجامعة الأردنية ليُكمل مرحلة الدراسات العليا في اللغة العربية وحصل على شهادتي الماجستير والدكتوراة في اللغة العربية تخصص نَحو ولغة عامي 2004 و2007، اشتُهر بروايته يا صاحبي السجن التي صدرت عام 2012 وتعبّر عن تجربة شخصيّة للكاتب في السجون الأردنية خلال عامَي 1996 و 1997 بكونه معتقلاً سياسياً. كما له دواوين شعريّة عديدة أحدثها ديوان \"طيور القدس عام 2016\".\r\n\r\n', 10, 'in stock', 'novels', 'sanad books', 'طريق جهنم.jfif', 'y', '2022-04-16 14:26:10'),
(36, 2, 1, 'شيفرة دافنشي', 'دان براون', 2003, 'سأل لانغدون، محدقاً في تيبينغ في المقعد الخلفي للليموزين. هناك دليل رمزي في شارع فليت؟ حتى الآن كان تيبينغ متكتماً بشكل مثير للأعصاب في ما يتعلق بالمكان الذي اعتقد أنهم قد يعثرون فيه على \"ضريح الفارس\" والذي سيجدون فيه الكلمة السرية التي ستفتح الكريبتكس الصغير بحسب كلمات القصيدة. ابتسم تيبينغ ثم التفت إلى صوفي. \"آنسة نوفو، امنحي صبي هارفرد هذا فرصة ثانية ودعيه يتفحص كلمات القصيدة مرة أخرى، هلا فعلت ذلك؟\". أدخلت صوفي يدها في جيبها وأخرجت منه الكريبتكس الأسود الذي كان ملفوفاً بجلد خزنة الماعز، حيث إنهم كانوا قد قرروا جميعاً ترك صندوق خشب الورد والكريبتكس الأكبر خزنة الطائرة وحملوا معهم ما يحتاجون إليه فقط وهو الكريبتكس الأسود الذي كان أكثر سهولة في الحمل وأقل لفتاً للأنظار. فردت صوفي الرق وأعطته إلى لانغدون. بالرغم من أن لانغدون كان قد قرأ القصيدة عدة مرات في الطائرة، إلا أنه لم يتمكن من الخروج منها بأي مكان محدد للضريح. أما الآن عندما بدأ يقرأ الكلمات ثانية، أخذ يستوعبها بعناية وبطء أكثر من قبل على أمل أن تكشف له التفعيلات الخماسية عن معنى أوضح الآن وقد أصبح على اليابسة. \"في لندن يرقد فارس دفنه بابا. جلبت له أعماله عقاباً إلهياً. أنت تبحث عن كرة ملكية موجودة على قبره. تحكي قصة جسد وردي ورحم حمل روحاً في قلبه\". كانت الكلمات بسيطة للغاية. فهناك فارس مدفون في لندن. فارس فعل شيئاً أغضب الكنيسة. فارس كان ضريحه يفتقد إلى كرة كان يجب أن تكون موجودة فيه. أما الإشارة الأخيرة في القصيدة-جسد وردي ورحم حمل روحاً في قلبه-فكانت تقصد مريم المجدلية الوردة التي حملت بذرة المسيح. وبالرغم من الصراحة والوضوح التي اتسمت به القصيدة، إلا أن لانغدون لا زال يجهل تماماً هوية ذلك الفارس والمكان الذي دفن فيه. إضافة إلى أنهم حتى إذا تمكنوا من العثور على الضريح، فهم لا زالوا يبحثون عن شيء غير موجود. الكرة التي يجب أن تكون على قبره؟\".ضمن أجواء غامضة يدور بحث البروفيسور لانغدون أستاذ علم الرموز الدينية في جامعة هارفرد عن سرّ الرسالة التي تركها جد صوفي خلف لوحة ليوناردو دافنشي \"مادونا أوف ذا روكس\" والتي كانت حلقة أخرى تضاف إلى سلسلة من الرموز المرتبطة ببعضها البعض، حيث كان جد صوفي سونيير ذا ولع شديد بالجانب الغامض والعبثي لليوناردو دافنشي. وفي الوقت ذاته كان اهتمام البروفيسور لانغدون وولعه شديداً بالرموز الدينية وبفكها، فكتبه حول الرسومات الدينية وعلم الرموز جعلت منه معارضاً مشهوراً في عالم الفن حتى غدا حضوره قوياً وشهرته واسعة وخاصة بعد تورطه في حادثة في الفاتيكان، تلك الحادثة أخذت أبعاداً إعلانية إلى درجة جعلت أهل الفن مهتمين به إلى أبعد الحدود. ينطلق لانغدون وصوفي في رحلة بحثية تمر في شوارع روما متوقفة عند كاتدرائياتها مروراً إلى باريس متوقفة عند متحف اللوفر في رحلة مشوقة لمعرفة سر رسالة جد صوفي والتي فتحت آفاقاً إلى اكتشاف سر الفارس المخلد في قصيدة دونت ضمن تلك الرسالة وذلك ضمن أجواء بوليسية شيقة وبأسلوب تميز به الكاتب دان براون في قصصه ذات الطابع البوليسي ويمكن القول بأن \"شيفرة دافنتشي\" هذه تتجاوز بتصنيفها الرواية البوليسية المثيرة بمراحل عديدة إذ أنها يلفها غموض ممتع مرتكز على أسرار بشكل ألغاز. تشد القارئ إلى درجة كبيرة متابعاً تفاصيل تلك الرواية بمزيد من المتع والذهول . ومما تجدر الإشارة إليه أخيراً هو أن هذه الرواية قد ترجمت إلى أكثر من 50 لغة وطبع منها أكثر من 8 ملايين نسخة وهذا ما يؤكد قول نيلسون دي ميل \"إن دان براون هو واحد من أفضل وأذكى وأكثر الكتاب براعة في البلاد. وشيفرة دافنتشي تتجاوز بتصنيفها الرواية البوليسية المثيرة بمراحل عديدة, إنها بحق عبقرية محضة\"', 'دان براون (Dan Brown) (مواليد 22 يونيو 1964 في ايكسيتير، نيوهامشير، الولايات المتحدة الأمريكية). مؤلف أمريكي لقصص الخيال والإثارة الممزوجة بطابع علمي وفلسفي حديث بأسلوب مشوق مكّنه من تحقيق أفضل المبيعات. حققت رواياته رواجاً كبيراً بين الأجيال الشابة في أمريكا وأوروبا، أشهر رواياته رواية شيفرة دا فينشي The Da Vinci Code التي نشرت عام 2003، وتم تصويرها كفيلم سينمائي من بطولة توم هانكس.', 12, 'in stock', 'novels', 'sanad books', 'شيفرة دافنشي.jfif', '', '2022-04-16 14:26:10'),
(37, 2, 1, 'شيفرة بلال', 'د.أحمد خيري العمري', 2016, 'ن تتأثر بقصة بلال بن رباح شيء, ولكن أن تتغير حياتك كلها بسبب ذلك شيء آخر تماما .. وأن يحدث ذلك في مجتمع عربي مسلم شيء, ول كن أن يحدث في نيويورك؟ !! رغم غرابته فهذا ما حدث ... قصة بلال بن رباح تغير مسار حياة أشخاص يعيشون في نيويورك , حياة بعيدة تماما عن أي تغيير وبالذات عن تغيير يأتي من قصة رجل مات قبل أكثر من ألف عام . لكن, ذات يوم ..يصل إيميل لأحدهم .., ويتغير كل شيء ... هذه الرواية هي قصة ما حدث معهم..بسبب (بلال) .. وما يمكن أن يحدث معك # شيفرة_بلال الرواية تبدأ بشكل مشوق جدا نستطيع ان نقول انه اشبه للحقيقة جدا فقط تبدأ بشكل مفاجئ من ايميل لطفل الى كاتب (طبعا بدون حرق للأحداث) من هذا تبدا كل شيء وبشكل مشوق جدا, التنقل بين الشخصيات وطريقة السرد فيه كانت جديدة علي وممتعة لا تحس ب الملل او الصعوبة وطبعا كل حدث يجذبك أكثر, وايضا استخدم الكاتب الكثير والكثير من الحقائق في هذه الرواية وخاصة تتكلم عن شخصية اسلامية كبيرة ومؤثرة مثل بلال الحبشي , وعلى رغم احساسي الكبير انه احداث حقيقية وليست رواية . الشيء الكبير الذي وجه هذه الرواية كانت بين عديد من المواضيع ولكن الاهم وحسب قراءتي هي , الايمان والالحاد (طبعا بشكل جميل وعدم اهانة اي منهم), وايضا عن العبودية والحرية ليست فقط عبودية السود في الماضي ولكن العديد من العبودية وعن قيمة الحرية لكل شخص منا . انصحكم جدا بقراءة الرواية وتكون بعيد جدا عن العنصرية لان مهما تكون سوف ت س د', 'ربية المتحدة - إمارة عجمان والتحق بـجامعة العلوم والتكنولوجيا الأردنية ليتحصل على بكالوريوس الهندسة المدنية فيها عام 1997 وفي عام 1999 تخرّج في جامعة اليرموك بشهادة بكالوريوس لغة عربية، ثمّ التحق بالجامعة الأردنية ليُكمل مرحلة الدراسات العليا في اللغة العربية وحصل على شهادتي الماجستير والدكتوراة في اللغة العربية تخصص نَحو ولغة عامي 2004 و2007، اشتُهر بروايته يا صاحبي السجن التي صدرت عام 2012 وتعبّر عن تجربة شخصيّة للكاتب في السجون الأردنية خلال عامَي 1996 و 1997 بكونه معتقلاً سياسياً. كما له دواوين شعريّة عديدة أحدثها ديوان \"طيور القدس عام 2016\".', 7, 'in stock', 'novels', 'sanad books', '33412025.jpg', '', '2022-04-16 14:26:10'),
(38, 2, 1, 'شقة الحرية', 'غازي القصيبي', 1999, 'في شقة الحرية يطوف الرمز... تخرج الشخصيات من أسمائها... وشقة الحرية من مكانها... والأحداث من زوايا أزمتها وتضحي شقة الحرية مكاناً لبقعة ما... والشخصيات كائنات لا على التعيين، يمكنها أن تأخذ أي مسمى، والأحداث يمكن أن تكون في أي زمن، والشقة هي أي بلد... ورغم كل ذلك فقد أرادها القصيبي شقة الحرية التي تحمل مضمونها في عنوانها... إنها الحرية التي جرت في مناخاتها الأحداث... حرية هؤلاء الظامئين إليها... إلى أي مدى نجح القصيبي في تحريرهم لاكتشاف مدى فهمهم لهذه الحرية من خلال استغلالهم لها؟!! واستغلالها لهم؟!! وشقة الحرية... مساحة فكرية تنقل فيها القصيبي من الغزل والسياسة إلى ذاك المناخ الثقافي والفني الغني. حيث يعيش مع بطل الرواية الذي يحاول كتابة القصة والانتماء إلى أهل الأدب، مشاهداته امتدت من صالون العقاد إلى جلسة نجيب محفوظ، مروراً بأنيس منصور... أفق رحب لأفكار غزيرة... وخيال جوال في التاريخ، في السياسة، في العلاقات الإنسانية، الروحية والجسدية وفي كل شيء... وروائي شاعر يغزل أحداثاً بحس الشاعر وبلغة الروائي الماهر لإبراز شيء ما، ولنقد واقع ما... إنه القصيبي كما هو... نسجه الروائي فسحة لمراجعة الحسابات.', 'غازي عبد الرحمن القصيبي، (2 مارس 1940 - 15 أغسطس 2010) شاعر وأديب وكاتب وسفير دبلوماسي ووزير سعودي، قضى سنوات عمره الأولى في مدينة \"الأحساء\" في \"المنطقة الشرقية\" من السعودية، ثم انتقل بعدها إلى العاصمة البحرينية المنامة ليدرس فيها مراحل التعليم، حصل على درجة البكالوريوس من كلية الحقوق في جامعة القاهرة، ثم حصل على درجة الماجستير في العلاقات الدولية من جامعة جنوب كاليفورنيا التي لم يكن يريد الدراسة بها، بل كان يريد دراسة القانون الدولي في جامعات أخرى من جامعات أمريكا، وبالفعل حصل على عدد من القبولات في جامعات عدة ولكن لمرض أخيه نبيل اضطر إلى الانتقال إلى جواره والدراسة في جنوب كاليفورنيا وبالتحديد في لوس أنجلوس ولم يجد التخصص المطلوب فيها فاضطر إلى دراسة العلاقات الدولية أما الدكتوراه ففي العلاقات الدولية من جامعة لندن والتي كانت رسالتها فيها حول اليمن كما أوضح ذلك في كتابه حياة في الإدارة.', 12, 'in stock', 'novels', 'sanad books', 'شيفرة دافنشي.jfif', '', '2022-04-16 14:26:10'),
(39, 2, 1, 'شرق المتوسط', 'عبد الرحمن منيف', 1975, 'هل يمكن أن ترمم إرادة انسان لم تعد تربطه بالحياة رابطة؟ أنا ذاك الإنسان. لا لست انساناً، السجن في أيامه الولى حاول أن يقتل جسدي. لم أكن أتصور أني أحتمل كل ما فعلوه، لكن احتملت. كانت إرادتي هي وحدها التي تتلقى الضربات، وتردها نظرات غاضبة وصمتاً. وظللت كذلك. لم أرهب، لم أتراجع: الماء البارد، ليكن. التعليق لمدة سبعة أيام، ليكن. التهديد بالقتل والرصاص حولي تناثر، ليكن. كانت ارادتي هي التي تقاوم. الآن ماذا بقي فيّ أو مني؟.', 'عبد الرحمن بن إبراهيم المنيف (و. عمّان صفر 1352ھ مايو 1933م، ت. دمشق ذو الحجة 1424ھ يناير 2004م،) هو خبير اقتصادي وأديب وناقد حداثي سعودي، عُد واحداً من أهم الكُتاب والرِوائيين العرب خلال القرن العشرين، وأشهر رُواة سيرة الجزيرة العربية المُعاصرة، وأحد أعمدة السرد العربي البارزة في العصر الحديث. ينتمي إلى بلدة قصيبا الواقعة على طريق عيون الجوى ‹شمال إمارة منطقة القصيم› في وسط الجزيرة العربية، السعودية. كان والده ‹إبراهيم العلي المنيف، ت. 1355ھ› من كبار تجار نجد (العقيلات) الذين اشتهروا برحلات التجارة بين القصيم والعراق، والشام. عُرِف ‹عبد الرحمن مُنيف وهذا اسم شهرته،› مفكراً وناشطاً سياسياً ‹حزبياً،› ثم خبيراً اقتصادياً ‹حاصلاً على درجة الدكتوراه،› وكاتباً صحفياً ‹محباً للفن التشكيلي،› ثم مؤلّفاً روائياً ‹منصرفاً عن أعمال الصحافة،› وقاصاً، وكاتب سيرِ.', 7, 'in stock', 'novels', 'unibookjo', 'sharq.jfif', '', '2022-04-16 14:26:10'),
(40, 2, 1, 'سلسلة وهج البنفسج', 'أسامة المسلم', 2017, 'نا من زمن الرسائل المختومة بطابع البريد ...زمن النظر المطول والسهر الاطول ... زمن الحديث بلا صوت والحزن بلا دموع ..... زمن انتهى ولا أظنه سيعود . \r\n\r\nالعلم الثابت ماهو إلا خيال أصابه الجمود .. هنا اجتمع الخيال مع عالم الكتب والمكتبات فكانت النتيجة رواية رائعة تحمل الكثير من المتعة والعبر', 'أسامة المِسّلِم كاتب وروائي سعودي من المنطقة الشرقية، في الأحساء من مواليد (15 ربيع الأول 1397هـ / 5 مارس 1977م) برز في وقت متأخر من حياته الأدبية في كتابة الروايات من نوع الفانتازيا والفانتازيا التاريخية  بقصص وأحداث ذات طابع تشويقي وبسرد الأحداث على الطريقة السينمائية ذات الحوارات المتينة بأسلوب السهل الممتنع.صدر له ما يقارب 17 رواية ويعتبر أشهرها روايته \"خوف\" و\"خوف 2\"  وسلسلة \"بساتين عربستان\" والتي صدر عنها حتى الآن خمس أجزاء، وسلسلة \"لج ملحمة البحور السبعة\" بخمس أجزاء، وسلسلة القصص القصيرة \"صخب الخسيف\" بثلاث أجزاء ، وجزء واحد من رواية \"الدوائر الخمس\" التابعة لسلسلة \"بساتين عربتسان، ورواية \"وهج البنفسج\" بجزأين.', 14, 'in stock', 'novels', 'sanad books', 'wahj.jfif', '', '2022-04-16 14:26:10');
